con:configuration.scriptText:
import groovy.json.JsonSlurper
def Order = context.testCase.getTestStepByName("Get All Orders").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Order)
def Count = holder.count.toInteger()
log.info Count

 for(def i=0;i<Count;i++)
 {
    if(holder.orders[i].product!=null)
{
 	if (holder.orders[i].name=="pressure cooker")
 	{
 		def uid = holder.orders[i]._id
 		log.info uid
 		def realuid = uid.toString()
 		context.testCase.testSuite.Project.setPropertyValue("OrderID",realuid)
 	}

 }
 }

import groovy.json.JsonSlurper
def Order = context.testCase.getTestStepByName("Get All Orders").getPropertyValue("Response")
def holder = new JsonSlurper().parseText(Order)
def Count = holder.count.toInteger()
log.info Count

for(def i=0;i<Count;i++)
{
	if (holder.orders[i].name=="coke")
	{
	  def ID = holder.orders[i]._id
	  log.info ID
	  def RealID = ID.toString()
	  context.testCase.testSuite.Project.setPropertyValue("RealID",RealID)
	}

}
